    private void cmdCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmdCalcularActionPerformed

        String Ley1, Seg2, For3, Caj4, SFinal;
        double Ley, Seg, For, Caj, Rest, ResF, Sbase;

        if (txtSueldoB.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Por Favor Digite El Sueldo Base", "Error", JOptionPane.ERROR_MESSAGE);
            txtSueldoB.requestFocusInWindow();
            txtSueldoB.selectAll();
        } else {

            Sbase = Double.parseDouble(txtSueldoB.getText());
            Ley = (Sbase * 0.01);
            Seg = (Sbase * 0.04);
            For = (Sbase * 0.005);
            Caj = (Sbase * 0.05);
            Rest = (Ley + Seg + For + Caj);
            ResF = (Sbase - Rest);

            Ley1 = String.valueOf(Ley);
            txtLey.setText(Ley1);
            Seg2 = String.valueOf(Seg);
            txtSSocial.setText(Seg2);
            For3 = String.valueOf(For);
            txtSForzoso.setText(For3);
            Caj4 = String.valueOf(Caj);
            txtCajaA.setText(Caj4);
            SFinal = String.valueOf(ResF);
            txtSueldoT.setText(SFinal);


        }

    }//GEN-LAST:event_cmdCalcularActionPerformed

    private void txtSueldoBKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtSueldoBKeyTyped

         char c = evt.getKeyChar();
        if (!Character.isDigit(c)) {
            getToolkit().beep();

            evt.consume();
        }
    }//GEN-LAST:event_txtSueldoBKeyTyped

